import java.util.Scanner;public class HQ1 {    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        System.out.print("Enter the value of m : ");        int m = hv.nextInt();        System.out.print("Enter the value of n : ");        int n = hv.nextInt();        int result = gcd(m, n);        System.out.println("GCD of " + m + " and " + n + " is: " + result);    }    public static int gcd(int m, int n) {        if (n == 0)            return m;        else            return gcd(n, m % n);    }}