import java.util.Scanner;public class HQ3 {    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        System.out.println("Enter the value of +ve decimal number: ");        int decimal = hv.nextInt();        String binary = decimalToBinary(decimal);        System.out.println("Binary equivalent of " + decimal + " is: " + binary);    }    public static String decimalToBinary(int decimal) {        if (decimal == 0)            return "0";        else if (decimal == 1)            return "1";        else            return decimalToBinary(decimal / 2) + decimal % 2;    }}