import java.util.Scanner;public class HQ4 {    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        System.out.println("Enter the value of first number : ");        int num1 = hv.nextInt();        System.out.println("Enter the value of second number : ");        int num2 = hv.nextInt();        int product = multiply(num1, num2);        System.out.println("Product of " + num1 + " and " + num2 + " is: " + product);    }    public static int multiply(int num1, int num2) {        if (num1 == 0 || num2 == 0)            return 0;        if (num2 == 1)            return num1;        return num1 + multiply(num1, num2 - 1);    }}