import java.util.Scanner;public class Q1 {    public static void main(String[] args) {        try (Scanner hv = new Scanner(System.in)) {            System.out.print("Enter your lucky number: ");            int luckyNumber = hv.nextInt();            if (luckyNumber < 0) {                throw new NumberFormatException("Negative number not allowed");            }            System.out.println("Your lucky number is: " + luckyNumber);        } catch (NumberFormatException e) {            System.out.println(e);        }    }}