import java.util.Scanner;class MarksOutOfBoundException extends Exception {    public MarksOutOfBoundException(String message) {        super(message);    }}class Student {    String name;    int marks;    Student(String a, int m) {        name = a;        marks = m;    }}public class Q3 {    public static void main(String[] args) throws MarksOutOfBoundException {        Student s1 = new Student("Harsha", 45);        System.out.println("Student name : " + s1.name + "\nmarks : " + s1.marks);        Scanner hv = new Scanner(System.in);        System.out.println("Enter John's marks");        int m = hv.nextInt();        if (m > 100) {            throw new MarksOutOfBoundException("Marks cannot be greater than 100");        } else {            Student s2 = new Student("John", m);            System.out.println("Student name : " + s2.name + "\nmarks : " + s2.marks);        }    }}