import java.util.Scanner;public class Q7 {    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        System.out.println("Enter the number");        int n = hv.nextInt();        long factorial = computeFactorial(n);        System.out.println("Factorial of " + n + " is: " + factorial);    }    public static long computeFactorial(int n) {        if (n == 0) {            return 1;        }        return n * computeFactorial(n - 1);    }}