import java.util.Scanner;public class Q8 {    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        System.out.println("Enter the real value of x");        double x = hv.nextDouble();        System.out.println("Enter a positive value of n");        int n = hv.nextInt();        double result = power(x, n);        System.out.println(x + " raised to the power of " + n + " is: " + result);    }    public static double power(double x, int n) {        if (n == 0)            return 1;        else if (n > 0)            return x * power(x, n - 1);        else            return 1 / power(x, -n);    }}