import java.util.Scanner;class Node {    int info;    Node next;    Node(int info) {        this.info = info;        this.next = null;    }}public class Q2 {    public static Node push(Node top) {        Scanner hv = new Scanner(System.in);        System.out.print("Enter the element to push: ");        int x = hv.nextInt();        Node newNode = new Node(x);        newNode.next = top;        top = newNode;        return top;    }    public static Node pop(Node top) {        if (top == null) {            System.out.println("Stack is empty! Cannot pop.");        } else {            System.out.println("Popped element: " + top.info);            top = top.next;        }        return top;    }    public static void display(Node top) {        if (top == null) {            System.out.println("Stack is empty!");        } else {            System.out.println("Stack elements are:");            Node temp = top;            while (temp != null) {                System.out.println(temp.info);                temp = temp.next;            }        }    }    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        Node top = null;        while (true) {            System.out.println("****MENU****");            System.out.println("0: Exit");            System.out.println("1: Push");            System.out.println("2: Pop");            System.out.println("3: Display");            System.out.println("Enter your choice:");            int choice = hv.nextInt();            switch (choice) {                case 0:                    System.exit(0);                case 1:                    top = push(top);                    break;                case 2:                    top = pop(top);                    break;                case 3:                    display(top);                    break;                default:                    System.out.println("Wrong choice");            }        }    }}