import java.util.Scanner;public class Q1 {    public static final int Max = 5;    public static int front = -1;    public static int rear = -1;    public static void insert(int[] Q) {        if (is_full()) {            System.out.println("Queue is full");            return;        }        Scanner hv = new Scanner(System.in);        System.out.print("Enter the element to insert: ");        int data = hv.nextInt();        if(front == -1 && rear == -1) {            front = rear = 0;            Q[rear] = data;        }        else {            Q[++rear] = data;        }        System.out.println("Inserted element is: " + data);    }    public static void delete(int[] Q) {        if (is_empty()) {            System.out.println("Queue is empty");            return;        }        int element = Q[front];        if (front == rear) {            front = rear = -1;        } else {            front++;        }        System.out.println("Deleted element is: " + element);    }    public static void display(int[] Q) {        if (is_empty()) {            System.out.println("Queue is empty.");            return;        }        System.out.print("Queue elements are: \n");        for (int i = front; i <= rear; i++) {            System.out.println(Q[i]);        }    }    public static boolean is_full() {        return rear == Max-1;    }    public static boolean is_empty() {        return front == -1 && rear ==  -1;    }    public static void main(String[] args) {        int[] queue = new int[Max];        Scanner hv = new Scanner(System.in);        while (true) {            System.out.println("***MENU***");            System.out.println("0: Exit");            System.out.println("1: Insert");            System.out.println("2: Delete");            System.out.println("3: Display");            System.out.println("Enter your choice");            int choice = hv.nextInt();            switch (choice) {                case 0:                    System.exit(0);                case 1:                    insert(queue);                    break;                case 2:                    delete(queue);                    break;                case 3:                    display(queue);                    break;                default:                    System.out.println("Invalid choice");            }        }    }}