import java.util.Scanner;class Node {    int info;    Node next;    public Node(int info) {        this.info = info;        this.next = null;    }}public class Q2 {    public static Node insert(Node rear, Node front) {        Scanner hv = new Scanner(System.in);        System.out.print("Enter the element to insert: ");        int ele = hv.nextInt();        Node newNode = new Node(ele);        if (front == null && rear == null) {            rear = front = newNode;        } else {            rear.next = newNode;        }        return front;    }    public static Node delete(Node rear, Node front) {        if (front == null && rear == null) {            System.out.println("Queue is empty");            return null;        }        assert front != null;        System.out.println("Deleted element is: " + front.info);        front = front.next;        return front;    }    public static void display(Node rear, Node front) {        if (front == null && rear == null) {            System.out.println("Queue is empty");            return;        }        Node temp = front;        System.out.print("Queue elements are: ");        while (temp != null) {            System.out.print(temp.info + " ");            temp = temp.next;        }        System.out.println();    }    public static void main(String[] args) {        Scanner hv = new Scanner(System.in);        Node rear = null, front = null;        while (true) {            System.out.println("****MENU****");            System.out.println("0: Exit");            System.out.println("1: Insert");            System.out.println("2: Delete");            System.out.println("3: Display");            System.out.println("Enter your choice");            int choice = hv.nextInt();            switch (choice) {                case 0:                    System.exit(0);                case 1:                    front = insert(rear, front);                    if (rear == null) {                        rear = front;                    } else if (rear.next != null) {                        rear = rear.next;                    }                    break;                case 2:                    front = delete(rear, front);                    break;                case 3:                    display(rear, front);                    break;                default:                    System.out.println("Wrong choice");            }        }    }}